// Handle batch data atau single data
const body = $input.item.json.body || $input.item.json;

// Cek apakah ini batch notification
if (body.batch && body.items && Array.isArray(body.items)) {
  // Batch mode: return array of items (setiap item menjadi output terpisah)
  const results = [];
  body.items.forEach(item => {
    results.push({
      json: {
        nama_bahan: item.nama_bahan,
        stok_bahan: parseFloat(item.stok_bahan || 0),
        id_bahan: item.id_bahan,
        satuan: item.satuan || '',
        min_stok: parseFloat(item.min_stok || 5),
        kategori: item.kategori || 'Tidak ada kategori',
        isBatch: true
      }
    });
  });
  return results; // Return array untuk multiple items
} else {
  // Single mode: backward compatible
  const stock = parseFloat(body.stok_bahan || body.stok || body.quantity || 0);
  return {
    json: {
      nama_bahan: body.nama_bahan || body.nama_produk || body.name || 'Produk Tidak Dikenal',
      stok_bahan: stock,
      id_bahan: body.id_bahan || body.id_produk || 'unknown',
      satuan: body.satuan || body.unit || '',
      min_stok: parseFloat(body.min_stok || body.minStock || 5),
      kategori: body.kategori || 'Tidak ada kategori',
      isBatch: false
    }
  };
}

// Ambil semua data dari input (bisa multiple items dari batch)
const items = $input.all();

// Cek apakah ini batch atau single
const isBatch = items.length > 1 || (items[0]?.json?.isBatch === true);

if (isBatch) {
  // Format pesan untuk batch (multiple items)
  let message = "ðŸš¨ *ALERT STOK MENIPIS* ðŸš¨\n\n";

  items.forEach((item, index) => {
    const data = item.json;
    message += `${index + 1}. *${data.nama_bahan}* (${data.kategori})\n`;
    message += `   Stok: ${data.stok_bahan} ${data.satuan}\n`;
    message += `   Minimum: ${data.min_stok} ${data.satuan}\n`;
    message += `   Kekurangan: ${(data.min_stok - data.stok_bahan).toFixed(2)} ${data.satuan}\n\n`;
  });

  message += "Segera lakukan restocking! ðŸ“¦";

  // Return single message untuk semua items
  return {
    json: {
      message: message,
      totalItems: items.length,
      isBatch: true,
      items: items.map(item => item.json)
    }
  };
} else {
  // Format pesan untuk single item (backward compatible)
  const data = items[0].json;
  const productName = data.nama_bahan;
  const stock = data.stok_bahan;
  const satuan = data.satuan || '';

  const message = `âš ï¸ Stok produk ${productName} tersisa ${stock}${satuan ? ' ' + satuan : ''}. Segera restock sebelum habis!`;

  return {
    json: {
      productName: productName,
      stock: stock,
      productId: data.id_bahan,
      satuan: satuan,
      message: message,
      isBatch: false,
      timestamp: new Date().toISOString()
    }
  };
}

// Ambil data
const data = $input.item.json;
const currentTime = Date.now();
const fiveMinutes = 5 * 60 * 1000;

// Untuk batch, gunakan key khusus
const memory = $getWorkflowStaticData('global');
if (!memory.lastNotification) {
  memory.lastNotification = {};
}

// Key untuk batch: "batch_" + timestamp (dibulatkan ke menit)
// Key untuk single: productId
const key = data.isBatch ? `batch_${Math.floor(currentTime / 60000)}` : (data.productId || data.items?.[0]?.id_bahan || 'unknown');

const lastSent = memory.lastNotification[key];

if (!lastSent || (currentTime - lastSent) > fiveMinutes) {
  memory.lastNotification[key] = currentTime;

  return {
    json: {
      ...data,
      shouldSend: true
    }
  };
} else {
  return {
    json: {
      ...data,
      shouldSend: false,
      skipReason: 'Notifikasi sudah dikirim dalam 5 menit terakhir'
    }
  };
}

// Ambil data dari node sebelumnya
const wahaResponse = $input.item.json;
const formatData = $('Format Pesan WhatsApp').item.json;

// Cek apakah ada error
const hasError = wahaResponse.error || (wahaResponse.statusCode && wahaResponse.statusCode >= 400);

if (hasError) {
  return {
    json: {
      success: false,
      message: 'Gagal mengirim notifikasi WhatsApp',
      productName: formatData.productName,
      stock: formatData.stock,
      error: wahaResponse.error || wahaResponse,
      timestamp: new Date().toISOString()
    }
  };
}

return {
  json: {
    success: true,
    message: 'Notifikasi WhatsApp berhasil dikirim',
    productName: formatData.productName,
    stock: formatData.stock,
    sentAt: new Date().toISOString(),
    wahaResponse: wahaResponse
  }
};

// Ambil data dari node sebelumnya
const data = $input.item.json;

// Pastikan stok_bahan dan min_stok adalah number
const stock = parseFloat(data.stok_bahan) || 0;
const minStock = parseFloat(data.min_stok) || 5; // Default 5 jika tidak ada

// Debug: log untuk melihat nilai
console.log('Stock value:', stock, 'Min stock:', minStock, 'Type:', typeof stock);

// Cek apakah stok < min_stok (bukan hardcoded 5)
if (stock < minStock) {
  // Jika stok menipis, return data untuk lanjut ke node berikutnya
  return {
    json: {
      ...data,
      stockCheck: true,
      stockValue: stock,
      minStockValue: minStock
    }
  };
} else {
  // Jika stok cukup, return null untuk skip (tidak lanjut ke node berikutnya)
  return null;
}

const data = $input.item.json;
const message = data.message || '';

// Escape karakter khusus untuk JSON
const escapedMessage = message
  .replace(/\\/g, '\\\\')
  .replace(/"/g, '\\"')
  .replace(/\n/g, '\\n')
  .replace(/\r/g, '\\r')
  .replace(/\t/g, '\\t');

return {
  json: {
    ...data,
    wahaRequest: {
      session: "default",
      chatId: "6281380630988@c.us",
      text: message
    }
  }
};
